-- ----------------------------
-- Table structure for `tp_content`
-- ----------------------------
DROP TABLE IF EXISTS `tp_content`;
CREATE TABLE `tp_content` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tid` int(11) NOT NULL COMMENT '上级',
  `uid` int(11) NOT NULL COMMENT '用户',
  `shows` tinyint(1) NOT NULL DEFAULT '1' COMMENT '显示',
  `choice` tinyint(1) NOT NULL DEFAULT '0' COMMENT '精贴',
  `settop` tinyint(1) NOT NULL DEFAULT '0' COMMENT '顶置',
  `praise` varchar(11) NOT NULL DEFAULT '0' COMMENT '赞',
  `view` varchar(11) NOT NULL DEFAULT '1' COMMENT '浏览量',
  `time` varchar(11) NOT NULL COMMENT '时间',
  `reply` varchar(11) NOT NULL DEFAULT '0' COMMENT '回复',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `pic` varchar(100) DEFAULT NULL COMMENT '图片',
  `keywords` varchar(100) DEFAULT NULL COMMENT '关键词',
  `description` varchar(200) DEFAULT NULL COMMENT '描述',
  `content` text COMMENT '内容',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of tp_content
-- ----------------------------
INSERT INTO `tp_content` VALUES ('12', '8', '1', '1', '0', '1', '0', '12', '1514025706', '0', 'php如何实现图片合并功能', '/uploads/20171223/1453e361a8e7e353ba8ef0d332b5113c.jpg', 'php,图片合并', '在实际的开发中需要用到合并图片的功能，比如实现微信中的头像图片合并功能。\n\n这里主要是使用的gd库去操作的，当然还可以用ImageMagick去实现这个功能。\n效果如下', '<p>在实际的开发中需要用到合并图片的功能，比如实现微信中的头像图片合并功能。</p><p>这里主要是使用的gd库去操作的，当然还可以用ImageMagick去实现这个功能。</p><p>效果如下：</p><p><img src=&quot;http://www.shuchengxian.com/Uploads/20171129/2017111511946658937344.jpg&quot; alt=&quot;如何用php gd库实现微信中的图片合并功能&quot;></p><p>php代码：</p><pre><ol><li>$pics&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'1.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'2.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'3.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'4.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'5.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'6.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'7.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'8.jpg\'</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\'9.jpg\'</li><li>);</li><li>&amp;nbsp;</li><li>$pic_list&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array_slice($pics&amp;nbsp;0&amp;nbsp;9);&amp;nbsp;//&amp;nbsp;只操作前9个图片</li><li>&amp;nbsp;</li><li>$bg_w&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;200;&amp;nbsp;//&amp;nbsp;背景图片宽度</li><li>$bg_h&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;200;&amp;nbsp;//&amp;nbsp;背景图片高度</li><li>&amp;nbsp;</li><li>$background&amp;nbsp;=&amp;nbsp;imagecreatetruecolor($bg_w$bg_h);&amp;nbsp;//&amp;nbsp;背景图片</li><li>$color&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;imagecolorallocate($background&amp;nbsp;255&amp;nbsp;255&amp;nbsp;255);&amp;nbsp;//&amp;nbsp;为真彩色画布创建白色背景，再设置为透明</li><li>imagefill($background&amp;nbsp;0&amp;nbsp;0&amp;nbsp;$color);</li><li>imageColorTransparent($background&amp;nbsp;$color);</li><li>&amp;nbsp;</li><li>$pic_count&amp;nbsp;&amp;nbsp;=&amp;nbsp;count($pic_list);</li><li>$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array();&amp;nbsp;&amp;nbsp;//&amp;nbsp;需要换行的位置</li><li>$space_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;3;</li><li>$space_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;3;</li><li>$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;0;</li><li>switch($pic_count)&amp;nbsp;{</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;1:&amp;nbsp;//&amp;nbsp;正中间</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/4);&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/4);&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/2);&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/2);&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;2:&amp;nbsp;//&amp;nbsp;中间位置并排</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;2;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/4)&amp;nbsp;+&amp;nbsp;3;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/2)&amp;nbsp;-&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/2)&amp;nbsp;-&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$space_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;3:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;40;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/2)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/2)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(2);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;4;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;4:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;4;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/2)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/2)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(3);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;4;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;5:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;30;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;30;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(3);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;6:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;30;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(4);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;7:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;53;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(25);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;8:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;30;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(36);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;9:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置X</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;开始位置Y</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_w&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_w/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_h&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;intval($bg_h/3)&amp;nbsp;-&amp;nbsp;5;&amp;nbsp;//&amp;nbsp;高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$lineArr&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;array(47);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line_x&amp;nbsp;&amp;nbsp;=&amp;nbsp;5;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>}</li><li>foreach(&amp;nbsp;$pic_list&amp;nbsp;as&amp;nbsp;$k=&amp;gt;$pic_path&amp;nbsp;)&amp;nbsp;{</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$kk&amp;nbsp;=&amp;nbsp;$k&amp;nbsp;+&amp;nbsp;1;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(&amp;nbsp;in_array($kk&amp;nbsp;$lineArr)&amp;nbsp;)&amp;nbsp;{</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;$line_x;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_y&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;$start_y&amp;nbsp;+&amp;nbsp;$pic_h&amp;nbsp;+&amp;nbsp;$space_y;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pathInfo&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;pathinfo($pic_path);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;switch(&amp;nbsp;strtolower($pathInfo[\'extension\'])&amp;nbsp;)&amp;nbsp;{</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;\'jpg\':</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;\'jpeg\':</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$imagecreatefromjpeg&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;\'imagecreatefromjpeg\';</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;\'png\':</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$imagecreatefromjpeg&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;\'imagecreatefrompng\';</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;case&amp;nbsp;\'gif\':</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;default:</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$imagecreatefromjpeg&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;\'imagecreatefromstring\';</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pic_path&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;file_get_contents($pic_path);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;break;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$resource&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;$imagecreatefromjpeg($pic_path);</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;$start_x$start_y&amp;nbsp;copy图片在背景中的位置</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;00&amp;nbsp;被copy图片的位置</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;$pic_w$pic_h&amp;nbsp;copy后的高度和宽度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;最后两个参数为原始图片宽度和高度，倒数两个参数为copy时的图片宽度和高度</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;imagecopyresized($background$resource$start_x$start_y00$pic_w$pic_himagesx($resource)imagesy($resource));</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$start_x&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;nbsp;$start_x&amp;nbsp;+&amp;nbsp;$pic_w&amp;nbsp;+&amp;nbsp;$space_x;</li><li>}</li><li>&amp;nbsp;</li><li>header(&quot;Content-type:&amp;nbsp;image/jpg&quot;);</li><li>imagejpeg($background);</li></ol></pre><p></p><p></p>');
INSERT INTO `tp_content` VALUES ('13', '8', '1', '1', '0', '1', '0', '15', '1514026138', '0', '解决thinkphp裁剪png图片背景不透明的方法', '/uploads/20171223/d3c5628b589f434ea4b93ad640845eeb.png', 'thinkphp,图片背景,透明', '在使用thinkphp对图片处理的时候，对于png图片的处理比如裁剪，缩略图，都会出现背景为白色，不是透明的情况。\n\n这里只是使用了thinkphp3.2.3版本，默认GD库处理，其他的类库自行解决哦。\n\n下面我们看，thinkphp裁剪png图片背景不透明的问题怎么解决？', '<p>在使用thinkphp对图片处理的时候，对于png图片的处理比如裁剪，缩略图，都会出现背景为白色，不是透明的情况。</p><p>这里只是使用了thinkphp3.2.3版本，默认GD库处理，其他的类库自行解决哦。</p><p>下面我们看，thinkphp裁剪png图片背景不透明的问题怎么解决？</p><p>其实，解决起来也是很简单的，只要将文件ThinkPHP/Library/Think/Image/Driver/GD.class.php修改其中的三个方法即可。</p>1、save()方法，第98行。<pre><ol><li>原代码：</li><li>&amp;nbsp;</li><li>$fun&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;\'image\'.$type;</li><li>$fun($this-&amp;gt;img&amp;nbsp;$imgname);</li><li>&amp;nbsp;</li><li>修改为：</li><li>&amp;nbsp;</li><li>$fun&amp;nbsp;&amp;nbsp;=&amp;nbsp;&amp;nbsp;&amp;nbsp;\'image\'.$type;</li><li>imagesavealpha($this-&amp;gt;img&amp;nbsp;true);</li><li>$fun($this-&amp;gt;img&amp;nbsp;$imgname);</li></ol></pre>2、crop()方法，第168行。<pre><ol><li>原代码：</li><li>&amp;nbsp;</li><li>$color&amp;nbsp;=&amp;nbsp;imagecolorallocate($img&amp;nbsp;255&amp;nbsp;255&amp;nbsp;255);</li><li>imagefill($img&amp;nbsp;0&amp;nbsp;0&amp;nbsp;$color);</li><li>&amp;nbsp;</li><li>修改为：</li><li>&amp;nbsp;</li><li>$color&amp;nbsp;=&amp;nbsp;imagecolorallocatealpha($img&amp;nbsp;0&amp;nbsp;0&amp;nbsp;0&amp;nbsp;127);</li><li>imagefill($img&amp;nbsp;0&amp;nbsp;0&amp;nbsp;$color);</li><li>imagesavealpha($img&amp;nbsp;true);</li></ol></pre>3、thumb()方法，第266行。<pre><ol><li>原代码：</li><li>&amp;nbsp;</li><li>$color&amp;nbsp;=&amp;nbsp;imagecolorallocate($img&amp;nbsp;255&amp;nbsp;255&amp;nbsp;255);</li><li>&amp;nbsp;</li><li>修改为：</li><li>&amp;nbsp;</li><li>$color&amp;nbsp;=&amp;nbsp;imagecolorallocatealpha($img&amp;nbsp;0&amp;nbsp;0&amp;nbsp;0&amp;nbsp;127);</li></ol></pre><p></p><p>通过修改上面三处方法，即可实现thinkphp裁剪png图片支持透明的效果了。</p><p></p>');
INSERT INTO `tp_content` VALUES ('14', '9', '1', '1', '0', '1', '0', '46', '1514026413', '0', 'phpstorm常用快捷键大全', '/uploads/20171223/d16331e16bfed7816c6a88604d619c3e.png', 'phpstorm,快捷键', 'ctrl+shift+F全局查找\nctrl+F      页内查找\n两次shift   搜索文件\n按住ctrl点击查找变量或函数\nctrl+r      批量替换\nctrl+/      行注释\nctrl+shift+/块注释\nctrl+alt+l  重新格式化代码\nctrl+x      剪切行\nctrl+shift+n查找文件\nctrl+shift+f在一个目录里查找一段代码(ctrl+f的升', '<p>整理phpstorm常用快捷键大全</p><p></p><pre><ol><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>F</span><span></span><span>全局查找</span><span></span></li><li><span>ctrl</span><span>+</span><span>F     </span><span></span><span> </span><span>页内查找</span><span></span></li><li><span>两次</span><span>shift</span><span></span><span>   </span><span>搜索文件</span><span></span></li><li><span>按住</span><span>ctrl</span><span>点击</span><span></span><span>查找变量或函数</span></li><li><span>ctrl</span><span>+</span><span>r</span><span></span><span>      </span><span>批量替换</span></li><li><span>ctrl</span><span>+/</span><span></span><span>      </span><span>行注释</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+/</span><span></span><span>块注释</span></li><li><span>ctrl</span><span>+</span><span>alt</span><span>+</span><span>l</span><span></span><span>  </span><span>重新格式化代码</span></li><li><span>ctrl</span><span>+</span><span>x</span><span></span><span>      </span><span>剪切行</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>n</span><span></span><span>查找文件</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>f</span><span></span><span>在一个目录里查找一段代码(</span><span>ctrl</span><span>+</span><span>f</span><span>的升级版)</span></li><li><span>ctr</span><span>+</span><span>shift</span><span>+</span><span>r </span><span></span><span>在一个目录里查找一段代码并替换(</span><span>ctrl</span><span>+</span><span>r</span><span>的升级版)</span></li><li><span>&amp;nbsp;</span></li><li><span>CTRL</span><span>+</span><span>ALT </span><span>←/→</span><span></span><span>返回上次编辑的位置</span></li><li><span>ALT</span><span>+</span><span> </span><span>←/→</span><span></span><span>    </span><span>切换代码视图，标签切换</span></li><li><span>ALT</span><span>+</span><span> </span><span>↑/↓</span><span></span><span>    </span><span>在方法间快速移动定位</span></li><li><span>ctrl</span><span>+</span><span>j        </span><span></span><span>插入活动代码提示</span></li><li><span>ctrl</span><span>+</span><span>alt</span><span>+</span><span>t</span><span></span><span>    </span><span>当前位置插入环绕代码</span></li><li><span>alt</span><span>+</span><span>insert</span><span></span><span>    </span><span>生成代码菜单</span></li><li><span>ctrl</span><span>+</span><span>q</span><span></span><span>        </span><span>查看代码注释</span></li><li><span>ctrl</span><span>+</span><span>d</span><span></span><span>        </span><span>复制当前行</span></li><li><span>ctrl</span><span>+</span><span>y</span><span></span><span>        </span><span>删除当前行</span></li><li><span>shift</span><span>+</span><span>F6     </span><span></span><span> </span><span>重命名</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>u</span><span></span><span>  </span><span>字母大小写转换</span></li><li><span>ctrl</span><span>+</span><span>f</span><span></span><span>       </span><span>查找</span></li><li><span>ctrl</span><span>+</span><span>r</span><span></span><span>       </span><span>替换</span></li><li><span>F4</span><span></span><span>           </span><span>查看源码</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>i</span><span></span><span>查看变量或方法定义源</span></li><li><span>ctrl</span><span>+</span><span>g</span><span></span><span>      </span><span>跳转行</span></li><li><span>ctrl</span><span>+</span><span>alt</span><span>+</span><span>F12</span><span></span><span>跳转至当前文件在磁盘上的位置</span></li><li><span>alt</span><span>+</span><span>down</span><span></span><span>    </span><span>查看下一个方法</span></li><li><span>alt</span><span>+</span><span>up      </span><span></span><span>查看上一个方法</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>downstatement </span><span>向下移动</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>upstatement   </span><span>向上移动</span></li><li><span>alt</span><span>+</span><span>shift</span><span>+</span><span>downline       </span><span>向下移动</span></li><li><span>alt</span><span>+</span><span>shift</span><span>+</span><span>upline         </span><span>向上移动</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>n</span><span></span><span>   </span><span>打开工程中的文件</span></li><li><span>ctrl</span><span>+</span><span>b</span><span></span><span>         </span><span>跳到变量申明处</span></li><li><span>ctrl</span><span>+[]</span><span></span><span>        </span><span>匹配</span><span> </span><span>{}[]</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+]/[</span><span></span><span> </span><span>选中块代码</span></li><li><span>ctrl</span><span>+</span><span>shift</span><span>+</span><span>v</span><span></span><span>   </span><span>复制多个文本</span></li><li><span>alt</span><span>+</span><span>left</span><span>/</span><span>right </span><span></span><span>标签切换</span></li><li><span>ctrl</span><span>+</span><span>p</span><span></span><span>         </span><span>显示默认参数</span></li><li><span>ctrl</span><span>+</span><span>F12</span><span></span><span>       </span><span>在当前类文件里快速查找方法</span><span> </span></li></ol></pre><p></p>');
INSERT INTO `tp_content` VALUES ('18', '15', '1', '1', '0', '0', '0', '12', '1514030367', '0', 'linux iptables防火墙的配置', '/uploads/20171223/f8bad8be8dd5cd0539f40478ff5143bf.jpg', 'iptables,防火墙,配置', '在服务器上端口的开放是有限的，为了更好的避免服务器被攻击，在服务器上必须限制开发的端口。\n\n一般我在用虚拟机的时候，都会直接将防火墙关闭。', '<p>在服务器上端口的开放是有限的，为了更好的避免服务器被攻击，在服务器上必须限制开发的端口。</p><p>一般我在用虚拟机的时候，都会直接将防火墙关闭。</p>一、查看防火墙的配置。<pre><ol><li>vi&amp;nbsp;/etc/sysconfig/iptables</li></ol></pre>二、配置防火墙的端口，开放80，22，3306。<p>将下面的代码直接复制进配置中</p><pre><ol><li>#&amp;nbsp;Firewall&amp;nbsp;configuration&amp;nbsp;written&amp;nbsp;by&amp;nbsp;system-config-firewall</li><li>#&amp;nbsp;Manual&amp;nbsp;customization&amp;nbsp;of&amp;nbsp;this&amp;nbsp;file&amp;nbsp;is&amp;nbsp;not&amp;nbsp;recommended.</li><li>*filter</li><li>:INPUT&amp;nbsp;ACCEPT&amp;nbsp;[0:0]</li><li>:FORWARD&amp;nbsp;ACCEPT&amp;nbsp;[0:0]</li><li>:OUTPUT&amp;nbsp;ACCEPT&amp;nbsp;[0:0]</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-m&amp;nbsp;state&amp;nbsp;--state&amp;nbsp;ESTABLISHEDRELATED&amp;nbsp;-j&amp;nbsp;ACCEPT</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-p&amp;nbsp;icmp&amp;nbsp;-j&amp;nbsp;ACCEPT</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-i&amp;nbsp;lo&amp;nbsp;-j&amp;nbsp;ACCEPT</li><li>#http</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-m&amp;nbsp;state&amp;nbsp;--state&amp;nbsp;NEW&amp;nbsp;-m&amp;nbsp;tcp&amp;nbsp;-p&amp;nbsp;tcp&amp;nbsp;--dport&amp;nbsp;80&amp;nbsp;-j&amp;nbsp;ACCEPT</li><li>#mysql</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-m&amp;nbsp;state&amp;nbsp;--state&amp;nbsp;NEW&amp;nbsp;-m&amp;nbsp;tcp&amp;nbsp;-p&amp;nbsp;tcp&amp;nbsp;--dport&amp;nbsp;3306&amp;nbsp;-j&amp;nbsp;ACCEPT</li><li>#ssh</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-m&amp;nbsp;state&amp;nbsp;--state&amp;nbsp;NEW&amp;nbsp;-m&amp;nbsp;tcp&amp;nbsp;-p&amp;nbsp;tcp&amp;nbsp;--dport&amp;nbsp;22&amp;nbsp;-j&amp;nbsp;ACCEPT</li><li>-A&amp;nbsp;INPUT&amp;nbsp;-j&amp;nbsp;REJECT&amp;nbsp;--reject-with&amp;nbsp;icmp-host-prohibited</li><li>-A&amp;nbsp;FORWARD&amp;nbsp;-j&amp;nbsp;REJECT&amp;nbsp;--reject-with&amp;nbsp;icmp-host-prohibited</li><li>COMMIT</li></ol></pre>三、重启防火墙，设置随系统启动。<pre><ol><li>service&amp;nbsp;iptables&amp;nbsp;restart&amp;nbsp;</li><li>&amp;nbsp;或者</li><li>/etc/init.d/iptables&amp;nbsp;restart</li><li>启动</li><li>chkconfig&amp;nbsp;iptables&amp;nbsp;on</li></ol></pre>四、关闭防火墙。<pre><ol><li>chkconfig&amp;nbsp;iptables&amp;nbsp;off</li></ol></pre><p></p><p></p>');
INSERT INTO `tp_content` VALUES ('15', '11', '1', '1', '0', '0', '0', '11', '1514026838', '0', '如何取消移动端的长按事件', '/uploads/20171223/dd457ec30dc127e3c96aecbbf9fe850e.png', '移动端,长按事件', '在app中嵌套h5界面，当用户长按的时候，会触发app端系统默认的交互行为。比如：\n\n1.长按文本区域显示文字放大镜，选择指定区域的网页内容，包括文字和图片,执行复制，粘贴等。\n2.长按链接对象呼出弹窗框，执行页面跳转或保存图片等', '<p>在app中嵌套h5界面，当用户长按的时候，会触发app端系统默认的交互行为。比如：</p><ul><li>1.长按文本区域显示文字放大镜，选择指定区域的网页内容，包括文字和图片执行复制，粘贴等。</li><li>2.长按链接对象呼出弹窗框，执行页面跳转或保存图片等</li></ul><p>这样的效果很影响美观，有时间用户感觉体验很差。</p>那么怎么解决禁止长按事件呢？<p>其实很简单那，只要在移动端页面只要加段js即可。</p><pre><ol><li>window.ontouchstart=function(){</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;document.documentElement.style.webkitTouchCallout=\'none\';</li><li>};</li></ol></pre>拓展：<p>在ios端使用UIWebView组件的情况下，也可以加上一下代码：</p><p></p><pre><ol><li><span>-</span><span>&amp;nbsp;</span><span>(</span><span>void</span><span>)</span><span>webViewDidFinishLoad</span><span>:(</span><span>UIWebView</span><span>*)</span><span>theWebView</span></li><li><span>{</span></li><li><span>[</span><span>self</span><span>.</span><span>webView&amp;nbsp;stringByEvaluatingJavaScriptFromString</span><span>:@</span><span>&quot;document.documentElement.style.webkitUserSelect=\'none\';&quot;</span><span>];</span></li><li><span>[</span><span>self</span><span>.</span><span>webView&amp;nbsp;stringByEvaluatingJavaScriptFromString</span><span>:@</span><span>&quot;document.documentElement.style.webkitTouchCallout=\'none\';&quot;</span><span>];</span><span>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;</span></li><li><span>}</span></li></ol></pre><p></p>');
INSERT INTO `tp_content` VALUES ('16', '12', '1', '1', '1', '1', '0', '64', '1514027137', '2', '利用jquery.qrcode.js生成二维码和logo居中的图片 js生成二维码', '/uploads/20171223/f2798aa4c37325e78960951edb9b385b.png', 'jquery,.qrcode.js,二维码,js生成二维码', '在项目中经常需要用到二维码的地方，如果都用服务器端生成的话就会很消耗资源。于是，直接通过JavaScript直接在客户端生成，有效减少带宽，以及维护成本。\n\n之前我介绍过用jquery.qrcode.js生成二维码的方法，这里是需要在生成二维码的中间加上logo。并且右键保存是一张图。', '<p>在项目中经常需要用到二维码的地方，如果都用服务器端生成的话就会很消耗资源。于是，直接通过JavaScript直接在客户端生成，有效减少带宽，以及维护成本。</p><p>之前我介绍过用jquery.qrcode.js生成二维码的方法，这里是需要在生成二维码的中间加上logo。并且右键保存是一张图。</p><p>前往&amp;nbsp;<a href=&quot;http://www.shuchengxian.com/article/631.html&quot; target=&quot;_blank&quot;>如何用jquery.qrcode.js插件在线生成二维码</a>&amp;nbsp;</p><p>官网上的 qrcode.js 文件里没有实现中文的支持和LOGO的添加，现在我们将这个文件修改为jquery.qrcode.js文件了。</p><p>此外还需要一个对中文进行转码的文件，该文件为utf.js。然后在jquery.qrcode.js 文件中调用了utf.js 文件的 utf16to8(str)方法对其中文进行了转码。</p><p>下面是我写的一个示例，该示例需要的js文件有jquery.qrcode.js和utf.js以及jquery-1.8.0.js&amp;nbsp;。还有一个LOGO图片可点击下载文件。</p><p>文件下载地址：</p><p>链接: https://pan.baidu.com/s/1eRT2JGe 密码: gm28</p><p>调用的html代码如下：</p><pre><ol><li>&amp;lt;!DOCTYPE&amp;nbsp;html&amp;nbsp;PUBLIC&amp;nbsp;&quot;-//W3C//DTD&amp;nbsp;HTML&amp;nbsp;4.01&amp;nbsp;Transitional//EN&quot;&amp;nbsp;&quot;http://www.w3.org/TR/html4/loose.dtd&quot;&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;html&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;head&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;me<x>ta&amp;nbsp;http-equiv=&quot;Content-Type&quot;&amp;nbsp;content=&quot;text/html;&amp;nbsp;charset=UTF-8&quot;&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;ti<x>tle&amp;gt;该二维码支持中文和LOGO&amp;lt;/ti<x>tle&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;script&amp;nbsp;type=&quot;text/javascript&quot;&amp;nbsp;src=&quot;jquery-1.8.0.js&quot;&amp;gt;&amp;lt;/script&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;script&amp;nbsp;type=&quot;text/javascript&quot;&amp;nbsp;src=&quot;utf.js&quot;&amp;gt;&amp;lt;/script&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;script&amp;nbsp;type=&quot;text/javascript&quot;&amp;nbsp;src=&quot;jquery.qrcode.js&quot;&amp;gt;&amp;lt;/script&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;script&amp;nbsp;type=&quot;text/javascript&quot;&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(document).ready(function()&amp;nbsp;{&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(&quot;#qrcodeCanvas&quot;).qrcode({&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;render&amp;nbsp;:&amp;nbsp;&quot;canvas&quot;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//设置渲染方式，有table和canvas，使用canvas方式渲染性能相对来说比较好&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;text&amp;nbsp;:&amp;nbsp;&quot;高蒙博客&quot;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//扫描二维码后显示的内容可以直接填一个网址，扫描二维码后自动跳向该链接&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;width&amp;nbsp;:&amp;nbsp;&quot;200&quot;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//二维码的宽度&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;height&amp;nbsp;:&amp;nbsp;&quot;200&quot;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//二维码的高度&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;background&amp;nbsp;:&amp;nbsp;&quot;#ffffff&quot;&amp;nbsp;//二维码的后景色&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreground&amp;nbsp;:&amp;nbsp;&quot;#000000&quot;&amp;nbsp;//二维码的前景色&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;src:&amp;nbsp;\'photo.jpg\'&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//二维码中间的图片&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;/script&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;/head&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;body&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;center&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;h2&amp;gt;该二维码支持中文和LOGO&amp;lt;/h2&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;div&amp;nbsp;id=&quot;qrcodeCanvas&quot;&amp;gt;&amp;lt;/div&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/center&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;/body&amp;gt;&amp;nbsp;&amp;nbsp;</li><li>&amp;lt;/html&amp;gt;</li></ol></pre><p></p><p>以上就是利用jquery.qrcode.js生成二维码和logo居中的图片，并且右键保存是一张图。</p>');
INSERT INTO `tp_content` VALUES ('17', '13', '1', '1', '0', '0', '0', '14', '1514029835', '0', ' 利用JQuery实现ajax异步刷新页面', '\'+data[i].pPic+\'', 'ajax,jquery', '在网站刷新的方法中，ajax刷新是一种用户体验良好的刷新方式，在结合ssh等流行的框架！最为常用了！下面就是常用一个ajax刷新表单的一个d', '<p>在网站刷新的方法中，ajax刷新是一种用户体验良好的刷新方式，在结合ssh等流行的框架！最为常用了！下面就是常用一个ajax刷新表单的一个demo，</p><pre><code>$(document).ready(function () {\n    testchaxun();\n     setInterval(&quot;testchaxun()&quot;5000);\n    });\n    function testchaxun(){\n        /* var selectId = $(\'#proviace\').val(); */\n        $.ajax({\n        type : &quot;POST&quot;\n         /* cache: false */\n        url : &quot;WarningAction!warningdefQuery1.action&quot;\n        dataType : &quot;json&quot;\n        success : function(data) {\n            $(\'tr\').remove(\'.datatr\');\n            for(var i=0;i&amp;lt;data.length;i++){\n                if(data[i].pState == \'危险\'){\n                    $(\'.table-thtr\').after(\'&amp;lt;tr class=&quot;datatr error-tr&quot;&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;&amp;lt;img class=&quot;picture&quot; src=&quot;\'+data[i].pPic+\'&quot;/&amp;gt;&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].pName+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].uName+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].bHomenum+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].bNumber+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].eStart+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].wType+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].wTime+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].pAddress+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].uName+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;\'+data[i].pState+\'&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;td&amp;gt;&amp;lt;a href=&quot;WarningAction!waringInfor.action?WId=\'+data[i].wId +\'&quot;&amp;gt;详情&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;\'+\n                      \'&amp;lt;/tr&amp;gt;\');\n                }else{\n                    $(\'.table-thtr\').after(\'&amp;lt;tr class=&quot;datatr&quot;&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;&amp;lt;img class=&quot;picture&quot; src=&quot;\'+data[i].pPic+\'&quot;/&amp;gt;&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].pName+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].uName+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].bHomenum+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].bNumber+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].eStart+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].wType+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].wTime+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].pAddress+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].uName+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;\'+data[i].pState+\'&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;td&amp;gt;&amp;lt;a href=&quot;WarningAction!waringInfor.action?WId=\'+data[i].wId+\'&quot;&amp;gt;详情&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;\'+\n                  \'&amp;lt;/tr&amp;gt;\');\n                }\n\n              }\n         }\n        });\n      };\n&amp;lt;/sc<x>ript&amp;gt;\n</code><ul><li></li></ul></pre><p>而在后台的WarningAction!waringInfor.action中接送两个参数，实现请求这个方法的必要条件！返回一个json</p><p></p><pre><code><span>public</span> String <span>waringInfor</span>(){\n         w= warningService.getWarningByWid(WId);\n         <span>if</span> (w!=<span>null</span>) {\n             <span>if</span> (w.getPId()!=<span>null</span>) {\n             p =patientService.getPatientByPId(w.getPId());\n            }\n        }\n        <span>if</span> (p!=<span>null</span>) {\n            <span>if</span> (p.getPId()!=<span>null</span>) {\n                s= sickroomService.getSickroomByPid(p.getPId());\n            }\n            <span>if</span> (p.getPEquipmentid()!=<span>null</span>) {\n                e= equipmentService.getEquipmentByEId(p.getPEquipmentid()); \n            }\n        }\n        <span>return</span> <span>&quot;waringInfor&quot;</span>;\n\n    };</code></pre>');
INSERT INTO `tp_content` VALUES ('21', '8', '1', '1', '0', '0', '0', '18', '1514092660', '0', '深入thinkphp5底层自动加载', 'http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8db43a8e84.png', 'thinkphp,加载机制,php加载,PHP', 'ThinkPHP5.0 真正实现了按需加载，所有类库采用自动加载机制，并且支持类库映射和composer类库的自动加载。\n\n自动加载的实现由think\\Loader类库完成，自动加载规范符合PHP的PSR-4。', '<p>ThinkPHP5.0 真正实现了按需加载，所有类库采用自动加载机制，并且支持类库映射和composer类库的自动加载。</p><p>自动加载的实现由think\\Loader类库完成，自动加载规范符合PHP的PSR-4。</p><p>自动加载</p><p>由于新版ThinkPHP完全采用了命名空间的特性，因此只需要给类库正确定义所在的命名空间，而命名空间的路径与类库文件的目录一致，那么就可以实现类的自动加载。</p><p>类库的自动加载检测顺序如下：</p><p>1、类库映射检测；2、PSR-4自动加载检测；3、PSR-0自动加载检测；</p><p>系统会按顺序检测，一旦检测生效的话，就会自动载入对应的类库文件。</p><p>首先我们打断点可以看到</p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8d9f560f10.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8d9f560f10.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8dabee8677.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8dabee8677.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8db43a8e84.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c8db43a8e84.png&quot;></a></p><p>默认没有Composer 可以到下方地址下载http://blog.csdn.net/wxqee/article/details/44983931Composer可以自动生成自动加载</p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c948cc6360d.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c948cc6360d.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9482128768.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9482128768.png&quot;></a></p><p>PSR-0与PSR-4的区别1.PSR-4是PSR-0的改进版2.PSR-0后面不一定要有反斜线， PSR-4必须要有反斜线 否则会报错如图：</p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c94af4ac4c4.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c94af4ac4c4.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c94b2ac15be.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c94b2ac15be.png&quot;></a></p><p>3.如果psr0最后一个类名，有下划线，会转换为分隔符sub_classname会转换成sub\\classname.php 而psr4并没有实际意义4.psr0的目录结构更深 如图</p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c94ded77586.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c94ded77586.png&quot;></a></p><p>类库映射 类名 和文件名要对应</p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d2a9dfa5.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d2a9dfa5.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d64ea782.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d64ea782.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d7a0c573.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d7a0c573.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d7f4e9d4.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c95d7f4e9d4.png&quot;></a></p><p>使用psr4命名空间加载</p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9611dc3a22.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9611dc3a22.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9614b5e132.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9614b5e132.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9616105625.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c9616105625.png&quot;></a></p><p><a href=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c961a20586e.png&quot; target=&quot;_blank&quot;><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-15/58c961a20586e.png&quot;></a></p><p></p><p>​</p><p></p>');
INSERT INTO `tp_content` VALUES ('19', '16', '1', '1', '0', '0', '0', '22', '1514030683', '0', '深入理解CPU和异构计算芯片GPU/FPGA/ASIC', '/uploads/20171223/72ec328038d66634ff5fb313564ea0ca.jpg', 'CPU,计算芯片,GPU,FPGA,ASIC,中央处理器 (CPU)', '随着互联网用户的快速增长，数据体量的急剧膨胀，数据中心对计算的需求也在迅猛上涨。诸如深度学习在线预测、直播中的视频转码、图片压缩解压缩以及HTTPS加密等各类应用对计算的需求已远远超出了传统CPU处理器的能力所及。摩尔定律失效的今天', '<p>随着互联网用户的快速增长，数据体量的急剧膨胀，数据中心对计算的需求也在迅猛上涨。诸如<a href=&quot;http://lixnk.zhihu.com/?target=https%3A//www.qcloud.com/document/product/560/8020%3FfromSource%3Dgwzcw.57428.57428.57428&quot; target=&quot;_blank&quot;>深度学习</a>在线预测、直播中的视频转码、图片压缩解压缩以及<a href=&quot;http://lixnk.zhihu.com/?target=https%3A//www.qcloud.com/document/product/228/6296%3FfromSource%3Dgwzcw.57429.57429.57429&quot; target=&quot;_blank&quot;>HTTPS</a>加密等各类应用对计算的需求已远远超出了传统CPU处理器的能力所及。摩尔定律失效的今天，关注“新“成员（GPU\\FPGA\\ASIC）为数据中心带来的体系架构变革，为业务配上一台动力十足的发动机。</p>1 异构计算：WHY<p>明明CPU用的好好的，为什么我们要考虑异构计算芯片呢？</p><p>随着互联网用户的快速增长，数据体量的急剧膨胀，数据中心对计算的需求也在迅猛上涨。诸如深度学习在线预测、直播中的视频转码、图片压缩解压缩以及HTTPS加密等各类应用对计算的需求已远远超出了传统CPU处理器的能力所及。</p><p>历史上，受益于半导体技术的持续演进，计算机体系结构的吞吐量和系统性能不断提高，处理器的性能每18个月就能翻倍（众所周知的“摩尔定律”），使得处理器的性能可以满足应用软件的需求。但是，近几年半导体技术改进达到了物理极限，电路越来越复杂，每一个设计的开发成本高达数百万美元，数十亿美元才能形成新产品投产能力。2016年3月24日，英特尔宣布正式停用“Tick-Tock”处理器研发模式，未来研发周期将从两年周期向三年期转变。至此，摩尔定律对英特尔几近失效。</p><p>一方面处理器性能再无法按照摩尔定律进行增长，另一方面数据增长对计算性能要求超过了按“摩尔定律”增长的速度。处理器本身无法满足高性能计算(HPC：High Performance Compute)应用软件的性能需求，导致需求和性能之间出现了缺口(参见图1)。</p><p>一种解决方法是通过硬件加速，采用专用协处理器的异构计算方式来提升处理性能。</p><p>图1 计算需求和计算能力的缺口发展形式</p>2 异构计算：STANDARDS<p>通常我们在为业务提供解决方案的时候，部署平台会有四种选择CPU、GPU、FPGA、ASIC。那有什么标准来评判计算平台的优劣呢？</p><p>图：我是法官标准我说了算</p><p>当今理想的协处理器应该是基于硬件的设计，具备三种基本能力。第一是设计能够提供专门的硬件加速实现各种应用中需要的关键处理功能。其次是协处理器设计在性能上非常灵活，使用流水线和并行结构，跟上算法更新以及性能的需求变化。最后，协处理器能够为主处理器和系统存储器提供宽带、低延迟接口。</p><p>除了硬件要求以外，理想的协处理器还应该满足HPC市场的“4P”要求：性能(performance)、效能(productivity)、功耗(power)和价格(price)。</p><p>HPC市场对性能的最低要求是全面加速实现算法，而不仅仅是某一步骤，并能够加速实现整个应用软件。</p><p>效能需求来自最终用户。在现有的计算机系统中，协处理器必须安装起来很方便，提供简单的方法来配置系统，加速实现现有的应用软件。</p><p>HPC市场的功耗需求来自计算系统安装和使用上的功耗限制。对于大部分用户，能够提供给计算机的空间有限。计算系统的功耗越小，那么可以采取更少的散热措施来保持计算机不会过热。因此，低功耗协处理器不但能够为计算系统提供更低的运转成本，而且还提高了计算系统的空间利用率。</p><p>价格因素在HPC市场上显得越来越重要。十几年前，某些应用软件对性能的需求超出了单个处理器能力范围，这促使人们采用专用体系结构，例如密集并行处理(MPP)和对称多处理(SMP)等。然而，这类系统要求使用定制处理器单元和专用数据通路，开发和编程都非常昂贵。</p><p>现在的HPC市场抛弃了如此昂贵的方法，而是采用性价比更高的集群计算方法。集群计算采用商用标准体系结构，例如Intel和AMD；采用工业标准互联，例如万兆以太网和InfiniBand；采用标准程序语言，例如运行在低成本Linux操作系统上的C语言等。当今的协处理器设计必须能够平滑集成到商用集群计算环境中，其成本和在集群中加入另一个节点大致相当。</p><p>了解了基本的评判标准之后，我们以当今最火的深度学习为例，从芯片架构、计算性能、功耗、开发难度几个方面来对几种不同的芯片进行分析对比。</p>3.2 芯片计算性能<p>深度学习的学名又叫深层神经网络（Deep Neural Networks），是从人工神经网络（Artificial Neural Networks）模型发展而来。我们以深度学习作为切入点来分析各个芯片的性能。图3是神经网络的基本结构，模型中每一层的大量计算是上一层的输出结果和其对应的权重值这两个矩阵的乘法运算。</p><p>图3 神经网络基本结构</p><p>横向对比CPU，GPU，FPGA，ASIC计算能力，实际对比的是：</p><p>1.硬件芯片的乘加计算能力。</p><p>2.为什么有这样乘加计算能力？</p><p>3.是否可以充分发挥硬件芯片的乘加计算能力？</p><p>带着这三个问题，我们进行硬件芯片的计算能力对比。</p><p>本文转载自<a href=&quot;http://lixnk.zhihu.com/?target=https%3A//www.qcloud.com/community/article/323436001490098149&quot; target=&quot;_blank&quot;>腾云阁<i></i></a>，已获得作者授权。</p><p></p>');
INSERT INTO `tp_content` VALUES ('20', '11', '1', '1', '0', '0', '0', '19', '1514033833', '0', 'css print', '/uploads/20171223/1c4673385d5d4bbc20642a9e6ce02fd5.jpg', 'css,print', '最近做表单打印，遂整理了一些打印相关的内容。\n\n说到网页打印，首先想到的便是@media查询（即网页css），通过使用媒体类型print即可解决实际应用的大多数问题，比如实现只打印网页的某部分内容，调整字体大小、修改布局等使打印出来的纸质文件更简洁明了。', '<p>最近做表单打印，遂整理了一些打印相关的内容。</p><p>说到网页打印，首先想到的便是@media查询（即网页css），通过使用媒体类型print即可解决实际应用的大多数问题，比如实现只打印网页的某部分内容，调整字体大小、修改布局等使打印出来的纸质文件更简洁明了。代码如下：</p><pre>@media print{\n\n    /*隐藏不打印的元素*/\n    .no-print{\n        display:none;\n    }\n    /*其他打印样式*/\n}</pre><p>但是，就打印表单来说，仅解决上述问题是不够的，我们无法忍受表单存在打印分页时内容被截断、分页显示顶部没有留白等问题。那么如何解决这些问题呢？这就要使用到css打印样式了，即@page，用来指定页面盒子的各个方面。</p><p>eg1：尺寸、页边距设置</p><pre>@page{\n     size: 5.5in 8.5in;       \n     margin: 30px;\n}</pre><p>注：这里除了可以用长度值声明尺寸，还可使用纸质尺寸关键字”A4″或“legal”；亦可通过关键字指定页面方向“portrait”、“landscape”，默认为portrait垂直方向。当margin设置不起作用时检查打印机是否边距是否设置了默认以外的值。</p><p>eg2：设置第一页、奇数页或偶数页样式（以首页为例）</p><pre>@page :first { \n\n    /*首页设置*/\n\n}</pre><p>注：left、right分别为偶数页、奇数页选择器。</p><p>eg3:避免表格断开</p><pre>@page{\n    table{\n        page-break-after: avoid;\n    }  \n}</pre><p>注：page-break-after对tr、td不起作用所以当以整体出现的时候要在同一个table中。</p><p>eg4:避免某行文字断裂</p><p></p><pre><span>@page</span><span>{</span><span>\n    table</span><span>{</span><span>\n        </span><span><span><span>page</span><span>-</span><span>break</span><span>-</span><span>inside</span></span></span><span><span>:</span><span><span><span> avoid</span><span>;</span></span></span></span><span>\n    </span><span>}</span><span>  \n</span><span>}</span></pre><p></p>');
INSERT INTO `tp_content` VALUES ('22', '8', '1', '1', '0', '0', '0', '27', '1514092797', '0', '详解访问如何隐藏index.php 也就是伪静态', 'http://www.thinkphp.cn/Uploads/editor/2017-03-11/58c3addec75cb.png', '伪静态,隐藏index.php', '在很多时候 我们都会有这样的需求 也是为了方便 在访问地址中不想让他显示 index.php 也可以继续访问 今天有时间给大家解答下\n这个也叫做伪静态 在我们没有配置这个的情况下 去掉index.php 就会出错 如图', '<p>在很多时候 我们都会有这样的需求 也是为了方便 在访问地址中不想让他显示 index.php 也可以继续访问 今天有时间给大家解答下这个也叫做伪静态 在我们没有配置这个的情况下 去掉index.php 就会出错 如图</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-11/58c3abe6b43af.png&quot; alt=&quot;&quot;></p><p>下面我来教大家来配置下伪静态 首先 我们要到环境中的 httpd.conf配置文件中 打开mod_rewrite.so模块 有人问如何开启？ 默认前边是加 # 号的是关闭 去掉就是开启 如图</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-11/58c3ac8143d4a.png&quot; alt=&quot;&quot;></p><p>开启之后 保存 一定要重启apache 这个是一定的 需要注意一下接着 找到你配置虚拟主机的位置 将 AllowOverride None 将None改为 All如图：</p><p>​<img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-11/58c3ad232ae08.png&quot; alt=&quot;&quot;></p><p>下面也是比较重要的一步了把下面的内容保存为.htaccess文件放到应用入口文件的同级目录下</p><ol><li></li><li>&amp;lt;IfModule mod_rewrite.c&amp;gt;</li><li>Options +FollowSymli<x>nks -Multiviews</li><li>RewriteEngine on</li><li></li><li>RewriteCond %{REQUEST_FILENAME} !-d</li><li>RewriteCond %{REQUEST_FILENAME} !-f</li><li>RewriteRule ^(.*)$ index.php?/$1 [QSAPTL]</li><li>&amp;lt;/IfModule&amp;gt;</li></ol><p>复制代码</p><p>如图</p><p></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-11/58c3addec75cb.png&quot; alt=&quot;&quot;></p><p></p>');
INSERT INTO `tp_content` VALUES ('23', '8', '1', '1', '0', '0', '0', '50', '1514092972', '0', '深入了解类延迟加载原理', 'http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be54850ab3d.png', '延迟加载,类', '所谓的延迟加载 其实就是 自动加载 通过注册加载函数__autoload() 或spl_autoload_register\n首先说下引入文件 require 与include的区别\n他们的区别就是 \nrequire 所包含的文件不存在 会报错 立即停止 执行\ninclude 所包含的文件不存在 会报错 继续往下执行', '<p>所谓的延迟加载 其实就是 自动加载 通过注册加载函数__autoload() 或spl_autoload_register首先说下引入文件 require 与include的区别他们的区别就是 require 所包含的文件不存在 会报错 立即停止 执行include 所包含的文件不存在 会报错 继续往下执行如图</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be4ca599521.png&quot; alt=&quot;&quot;></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be4cac959aa.png&quot; alt=&quot;&quot;></p><p>a.php 和b.php 内容一致</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be4ce47d3f7.png&quot; alt=&quot;&quot;></p><p>使用__autoload如何实现自动加载</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be50652d144.png&quot; alt=&quot;&quot;></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be50dead957.png&quot; alt=&quot;&quot;></p><p>执行流程 可以打断点 这是我以前写的http://www.thinkphp.cn/topic/46349.html在这里就不说了再来看看看 spl_autoload_register</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be519394ba6.png&quot; alt=&quot;&quot;></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be5289313e3.png&quot; alt=&quot;&quot;></p><p>闭包</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be54850ab3d.png&quot; alt=&quot;&quot;>​</p><p>执行结果</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-07/58be54a4127b0.png&quot; alt=&quot;&quot;></p><p></p><p>记得一定要使用断点 看执行流程</p><p></p>');
INSERT INTO `tp_content` VALUES ('24', '8', '1', '1', '0', '0', '0', '45', '1514093073', '1', '详解命名空间 似懂非懂的进来听听~ 	 ', 'http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69d42c4de4.png', 'PHP,命名空间', '今天说下命名空间\n\n所谓的命名空间 就类似与目录 同一个目录下 可以用不同名字的文件 但是绝对不可能出现 两个一模一样的文件 比如说\n\n正确： a目录下边有一个 a.txt 和b.txt\n\n错误： a目录下存在两个a.txt', '<p>今天说下命名空间所谓的命名空间 就类似与目录 同一个目录下 可以用不同名字的文件 但是绝对不可能出现 两个一模一样的文件 比如说正确： a目录下边有一个 a.txt 和b.txt错误： a目录下存在两个a.txt今天所说的话题也是一样的道理 如图 ：</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69a53407f9.png&quot; alt=&quot;&quot;></p><p>肯定是错误的 报错如下：</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69a790c26b.png&quot; alt=&quot;&quot;></p><p>那么 在这个时候就要使用到命名空间了 先说明下使用第一个命名空间注意事项不能再命名空间面前加任何内容 如html 标签语言等等 需要注意的是 空格也不行 如图</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69b318b4ca.png&quot; alt=&quot;&quot;></p><p>报错如下：</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69b3c7936b.png&quot; alt=&quot;&quot;></p><p>为什么说是第一个呢！ 因为在一个文件中 可以有多个命名空间 比如这样：</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69c33b5d8d.png&quot; alt=&quot;&quot;></p><p>这样也就避免了之前所说的问题了</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b69d42c4de4.png&quot; alt=&quot;&quot;></p><p>模式分为3种1.限定名称如 namespace xxx\\xxx; 可以看做是相对路径2.非限定名称如 namespace xxx;3.完全限定命名空间类似与绝对路径 如\\aaa\\bbb\\ccc;当公共空间 引入 非公共空间文件时，前边不加反斜杠会报错 如图</p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b6a09589c36.png&quot; alt=&quot;&quot;></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b6a0afe33bf.png&quot; alt=&quot;&quot;></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b6a0d6ddeb7.png&quot; alt=&quot;&quot;></p><p>这个才是正确的在引入命名空间的时候需要注意下 如果 出现同名 可以使用 as 设置别名如下</p><p></p><p><img src=&quot;http://www.thinkphp.cn/Uploads/editor/2017-03-01/58b6a25983d8e.png&quot; alt=&quot;&quot;></p><p></p>');
INSERT INTO `tp_content` VALUES ('27', '15', '37', '1', '0', '0', '0', '36', '1515410449', '1', '教你一招：内网如何通过外网访问', '/uploads/20180109/0dcb79521de372ba0db43e617244952d.jpg', '内网,外网,VPS,访问', '很多人的电脑目前宽带拨号，或者在公司，都是内网IP，也就是说你只能连接别人，别人不能连接你\n\n其原理跟花生壳差不多一样，但是还是需要点代价，就是自己还得必须准备一个VPS（支持公网的）', '<p></p><p><img style=&quot;max-width: 100%; width: 951px; height: 353px;&quot; src=&quot;/uploads/20180109/0dcb79521de372ba0db43e617244952d.jpg&quot; class=&quot;&quot;></p><p><img src=&quot;/uploads/20180109/225968702857e4b420ceb5522da39bc5.gif&quot; alt=&quot;20180109114717681768&quot; style=&quot;max-width: 100%; width: 944px; height: 459px;&quot; class=&quot;&quot;></p><p></p><p><b><font size=&quot;5&quot;>很多人的电脑目前宽带拨号，或者在公司，都是内网IP，也就是说你只能连接别人，别人不能连接你</font></b></p><p><b><font size=&quot;5&quot;>其原理跟花生壳差不多一样，但是还是需要点代价，就是自己还得必须准备一个VPS（支持公网的）</font></b></p><p><b><font size=&quot;5&quot;>本次开源的源码里面，是客户端与服务器结合在一起了，服务器上面挂一个，自己电脑上面挂一个</font></b></p><p><b><font size=&quot;5&quot;>映射地址，也就是服务器的公网IP地址，输入点击链接之后， 内网服务器就成功接入到了VPS服务器</font></b></p><p><b><font size=&quot;5&quot;>之后通过公网地打开，那么就能成功通过公网VPS服务器访问到你的内网服务器，达到一个中间人作用</font></b></p><p></p>\n\n\n\n     \n         \n          <a href=&quot;https://pan.baidu.com/s/1kVTcNUv&quot; style=&quot;position: relative;z-index: 1;display: inline-block;height: 37px; line-height: 37px; padding-left: 20px; padding-right: 20px;margin-right: 10px;margin-bottom: 10px;\n    border: 1px solid #d7d7d7;border-width: 1px 1px 4px; font-size: 14px; font-weight: 700;color: #0b588c;text-decoration: none;-moz-transition: none;-webkit-transition: none; -o-transition: none;transition: none;border-radius: 2px;&quot;><i class=&quot;ico&quot;></i><i class=&quot;line&quot;></i>百度网盘(密码：hkec)</a> &amp;nbsp;<p></p>');
INSERT INTO `tp_content` VALUES ('28', '11', '39', '1', '0', '0', '0', '3', '1558919354', '30', 'PHP测试', '/uploads/20190527/3758b57835c640ce81e5c7a5f143444e_thumb_240_160.jpg', 'LINUX,PHP', '大晚上的来不及啊额我我去夫人请安违反人防区微软分为七个富特文而非提前发个', '<p><img src=&quot;/uploads/20190527/3758b57835c640ce81e5c7a5f143444e.jpg&quot; alt=&quot;bg0&quot; style=&quot;max-width:100%;&quot;>大晚上的来不及啊额我我去夫人请安违反人防区微软分为七个富特文而非提前发个</p><p></p>');

